package NumberPatternPgms;

public class program3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int count=5;
		for(int i=1;i<=count;i++){
		for(int j=1;j<=i;j++){
		System.out.print(i);
		}
		System.out.println();
		}
	}

}

//output
//1
//22
//333
//4444
//55555
